'use strict';


const keypad = [
  [-1, -1, 1, -1, -1],
  [-1,  2, 3,  4, -1],
  [ 5,  6, 7,  8,  9],
  [-1, 'A', 'B', 'C', -1],
  [-1, -1, 'D', -1, -1]
];

const input = `



`;


function goToBathroom() {
  let currentRow = 2;
  let currentCol = 0;

  const password = [];

  const keys = input.split('\n');
  for(let i = 0; i < keys.length; ++i) {
    for(let j = 0; j < keys[i].length; ++j) {
      switch(keys[i][j]) {
        case 'U':
          currentRow = currentRow - 1 >= 0 && keypad[currentRow - 1][currentCol] !== -1 ?
            currentRow - 1 :
            currentRow;
          break;
        case 'D':
          currentRow = currentRow + 1 <= 4 && keypad[currentRow + 1][currentCol] !== -1 ?
            currentRow + 1 :
            currentRow;
          break;
        case 'L':
          currentCol = currentCol - 1 >= 0 && keypad[currentRow][currentCol - 1] !== -1 ?
            currentCol - 1 :
            currentCol;
          break;
        case 'R':
          currentCol = currentCol + 1 <= 4 && keypad[currentRow][currentCol + 1] !== -1 ?
            currentCol + 1 :
            currentCol;
          break;
        default:
          console.log('ERROR');
          return;
      }
      console.log(currentRow, currentCol);
      console.log(keypad[currentRow][currentCol]);
    }
    password.push(keypad[currentRow][currentCol]);
  }
  console.log(password);
}

goToBathroom();
